class Solution:
    # @return an integer
    def lengthOfLongestSubstring(self, s):
        """
        working 
        """
        result = 0
        i = 0
        ptr = 0
        flag = True
        while i < len(s):
            if flag:
                while ptr < len(s):
                    if s[ptr] not in s[i:ptr]:
                        ptr += 1
                    else:
                        result = max(result, ptr-i)
                        flag = False
                        break
            if ptr == len(s):
                break
            if s[i] == s[ptr]:
                flag = True
            i += 1
        return max(result, ptr-i)

    def lengthOfLongestSubstring2(self, s):
        i=0
        m={}
        n=len(s)
        j=0
        max_len=0
        while j < n:
            #print s[j]
            if s[j] in m:
                max_len=max(max_len, j-i)
                while s[i] != s[j]:
                    m[s[i]] = False
                    i+=1
                i+=1
                j+=1
            else:
                m[s[j]] = True
                j+=1
               
        return max(max_len,n-i)

